CREATE TABLE
    "SMARTPARKINGDB"."SEASONPASS" (
        "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY PRIMARY KEY,
        "TITLE" VARCHAR2 (250 BYTE),
        "DESCRIPTION" VARCHAR2 (500 BYTE),
        "PRICE" NUMBER,
        "DURATION" VARCHAR2 (50 BYTE),
        "PETAKDIGIT" VARCHAR2 (250 BYTE),
    );

CREATE TABLE
    "SMARTPARKINGDB"."SEASONAL_PURCHASES" (
        "IDSEASONALTRANSACTION" VARCHAR2 (250 BYTE) PRIMARY KEY,
        "SEASONPASSID" NUMBER,
        "BUYDATETIME" VARCHAR2 (250 BYTE),
        "PAYMENTMETHOD" VARCHAR2 (250 BYTE),
        "PAYMENTSTATUS" VARCHAR2 (250 BYTE),
    )
CREATE TABLE
    "SMARTPARKINGDB"."SEASONAL" (
        "ID" NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
        "IDSEASONALTRANSACTION" VARCHAR2 (250 BYTE),
        "USERID" VARCHAR2 (250 BYTE),
        "VEHICLEID1" VARCHAR2 (250 BYTE),
        "VEHICLEID2" VARCHAR2 (250 BYTE),
    );

COMMIT;