CREATE TABLE
    "SMARTPARKINGDB"."PUBLICUSER" (
        "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY PRIMARY KEY,
        "USERID" VARCHAR2 (250 BYTE) UNIQUE,
        "USERNAME" VARCHAR2 (250 BYTE),
        "EMAIL" VARCHAR2 (250 BYTE),
        "PASSWORD" VARCHAR2 (250 BYTE),
        "ACCOUNTSTATUS" VARCHAR2 (250 BYTE) DEFAULT 'UNVERIFIED',
        "LOGINSTATUS" VARCHAR2 (250 BYTE) DEFAULT 'LOG OUT',
        "CREATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
        "UPDATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP
    );

COMMIT;

CREATE TABLE
    "SMARTPARKINGDB"."USER_PERSONAL" (
        "USERID" VARCHAR2 (250 BYTE) PRIMARY KEY,
        "FIRSTNAME" VARCHAR2 (250 BYTE),
        "LASTNAME" VARCHAR2 (250 BYTE),
        "PHONENUMBER" VARCHAR2 (50 BYTE),
        "CREATED_AT" VARCHAR2 (250 BYTE),
        "UPDATED_AT" VARCHAR2 (250 BYTE)
    );

COMMIT;

