CREATE TABLE
        "SMARTPARKINGDB"."OPERATOR_ATTENDANCE" (
                "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY PRIMARY KEY,
                "OPERATOR_ID" VARCHAR2 (250 BYTE),
                "ZONE_ID" VARCHAR2 (250 BYTE),
                "DATE" VARCHAR2 (250 BYTE) DEFAULT TO_CHAR (CURRENT_TIMESTAMP, 'YYYY-MM-DD'),
                "CLOCK_IN" VARCHAR2 (250 BYTE) DEFAULT TO_CHAR (CURRENT_TIMESTAMP, 'HH24:MI:SS'),
                "CLOCK_OUT" VARCHAR2 (250 BYTE),
                "CREATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                "UPDATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP
        );