        "OPERATORID" VARCHAR2 (255 BYTE),
        "SUPERADMINID" VARCHAR2 (255 BYTE),
        "STATUS" VARCHAR2 (255 BYTE) DEFAULT 'PENDING',
        "CREATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
        "UPDATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP
    );